import React from "react";
import "./UserMenu.css";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";

const UserMenu = props => {
  return (
    <div className="UserMenu" onClick={() => props.closeFullscreen()}>
      <p className="greeting">Hello, {props.name}.</p>
      {this.props.user.authenticatedUser.roles[0].name === "ROLE_ADMIN" ? (
        <React.Fragment>
          <Link to={"/secure/users"}>Manage Users</Link>
          <Link to={"/secure/categorization"}>Manage Categorization</Link>
          <Link to={"/secure/products"}>Manage Products</Link>
          <Link to={"/secure/orders"}>Manage Orders</Link>
        </React.Fragment>
      ) : null}
      <Link to={"/user"}>Settings</Link>
      <div
        className="logout"
        onClick={() => {
          document.cookie =
            "Session=; expires= Thu, 21 Aug 2014 20:00:00 UTC; path=/ ";
          props.history.push({ pathname: "/" });
        }}
      >
        Log Out
      </div>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    user: state.user
  };
};

export default connect(mapStateToProps)(withRouter(UserMenu));
