import React from "react";
import "./ProductInfo.css";

export default function ProductInfo(props) {
  return (
    <div className="ProductInfo">
      <header>
        <h1 className="brand">{props.product.attributes.brand}</h1>
        <h1 className="model">{props.product.name}</h1>
      </header>
      <section>
        <div className="color">
          <h3>Color</h3>
          <div style={{ backgroundColor: props.product.attributes.color }} />
        </div>
        <div className="size">
          <h3>Size</h3>
          <select>
            <option value="medium">Medium</option>
            <option value="small">Small</option>
            <option value="big">Big</option>
          </select>
        </div>
        <div className="quantity">
          <h3>Quantity</h3>
          <h4>{props.product.quantity} units</h4>
        </div>
        <div className="price">
          <h3>Price</h3>
          <h4>{props.product.price} â‚¬</h4>
        </div>
        <div className="description">
          <h3>Description</h3>
          <i
            className="fas fa-plus btn-plus"
            id="btn-plus"
            onClick={() => {
              if (
                document.getElementById("description").style.display === "block"
              ) {
                document.getElementById("btn-plus").style.transform =
                  "rotate(90deg)";
                document.getElementById("description").style.display = "none";
              } else {
                document.getElementById("btn-plus").style.transform =
                  "rotate(45deg)";
                document.getElementById("description").style.display = "block";
              }
            }}
          />
          <p id="description">{props.product.description}</p>
        </div>
        <div className="btn-list">Add to list</div>
        <div className="btn-basket">Add to basket</div>
      </section>
    </div>
  );
}
