import React, { Component } from "react";
import "./Users.css";
import axios from "axios";

export default class Users extends Component {
  state = {
    users: []
  };
  getCookie(cname) {
    let name = cname + "=";
    let ca = document.cookie.split(";");
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) === " ") {
        c = c.substring(1);
      }
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  }
  componentDidMount() {
    let url = `http://localhost:8087/secure/users?access_token=${this.getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
        console.log(res.data);
        
      this.setState({
        users: res.data
      });
    });
  }
  render() {
    return (
      <div className="Users">
        {this.state.users.length === 0 ? null : (
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Password</th>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Address</th>
                <th>Enable</th>
                <th>Edit</th>
              </tr>
            </thead>
            <tbody>
              {this.state.users.map(user => (
                <tr key={user.id}>
                  <td>{user.username}</td>
                  <td>{user.password}</td>
                  <td>
                    {user.name} {user.surname}
                  </td>
                  <td>{user.email}</td>
                  <td>{user.phone}</td>
                  <td>{user.address}</td>
                  <td>{console.log(user.enabled)
                  }</td>
                  <td>
                    <img className="edit" alt="Edit Icon" src="" />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}
