import React, { Component } from "react";
import "./Orders.css";
import axios from "axios";

class Orders extends Component {
  state = {
    products: [],
    quantityLs: [],
    total: 0
  }
  componentDidMount() {
    if (localStorage.getItem("product") !== null) {
      let products = localStorage.getItem("product").split(",");
      axios({
        method: "post",
        headers: { "content-type": "application/json" },
        data: products,
        url: "http://localhost:8086/products/arr"
      })
        .then(res => {
          let quantityLs = localStorage.getItem("quantity").split(",");
          this.setState({ products: res.data, quantityLs: quantityLs });
          this.calculateTotalHandler();
        });
    }
  }
  removeFromCartLs(productId) {
    let products = this.state.products;
    for (let p = 0; p < products.length; p++) {
      if (products[p].id === productId) {
        products.splice(p, 1);
      }
    }
    let productsLs = localStorage.getItem("product").split(",");
    let quantityLs = localStorage.getItem("quantity").split(",");
    const index = productsLs.indexOf(productId);
    productsLs.splice(index, 1);
    quantityLs.splice(index, 1);
    localStorage.setItem("product", productsLs);
    localStorage.setItem("quantity", quantityLs);
    this.setState({
      products: products,
      quantityLs: quantityLs
    })
  }
  calculateTotalHandler() {
    const products = this.state.products;
    const quantity = localStorage.getItem("quantity").split(",");
    let total = 0;
    for (let p = 0; p < products.length; p++) {
      total += (products[p].price * quantity[p]);
    }
    total = (Math.round(total * 100) / 100)
    this.setState({ total: total });
  }
  navigateProduct(product) {
    this.props.history.push({ pathname: `/categories/subcategories/${product.id}` });
  }
  render() {
    return <div className="Orders">
      <div className="cart-products">
        <div className="cart-products-wrapper">
          <table>
            <thead>
              <tr>
                <th>Item</th>
                <th>Description</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {this.state.products.length !== 0 ? this.state.products.map((product, index) => <tr key={product.id} >
                <td>
                  <div className="frame-img" onClick={() => this.navigateProduct(product)}>
                    <img src={product.thumbnail} alt={product.id}></img>
                  </div>
                </td>
                <td><div onClick={() => this.navigateProduct(product)} className="details">
                  <div className="product-brand">{product.attributes.brand}</div>
                  <div className="product-name">{product.name}</div>
                </div></td>
                <td>{"€" + product.price}</td>
                <td>
                  <input className="quantity-input" type="number" name="quantityLs" min="0" value={this.state.quantityLs[index]} max={product.quantity} onChange={(e) => {
                    if (e.target.value < 1) {
                      this.removeFromCartLs(product.id);
                    } else {
                      let quantityLs = this.state.quantityLs;
                      quantityLs[index] = e.target.value;
                      this.setState({ quantityLs: quantityLs });
                      localStorage.setItem("quantity", quantityLs);
                    }
                    this.calculateTotalHandler();
                  }}
                    onKeyPress={(event) => event.preventDefault()}
                  ></input>
                </td>
                <td>{"€" + (Math.round(product.price * this.state.quantityLs[index] * 100) / 100)}</td>
              </tr>) : <tr><td colSpan="5" className="no-products">Your cart is empty!</td></tr>}
            </tbody>
          </table>
        </div>
      </div>
      <div className="price">
        <div className="price-wrapper">
          <div className="total-price">
            <p>Total to pay</p>
            <p className="price-number">€{this.state.total}</p>
          </div>
          <button className="checkout-btn">Proceed to checkout</button>
        </div>
      </div>
    </div>;
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchAuthenticatedUserLogin: (username, password) =>
      dispatch(fetchAuthenticatedUserLogin(username, password))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
