import React, { Component } from "react";
import "./CreateProduct.css";
import axios from "axios";

export default class CreateProduct extends Component {
  state = {
    categories: {},
    selectedCategorie: ""
  };
  componentDidMount() {
    let url = `http://localhost:8086/catalogs/categories`;
    axios.get(url).then(res => {
      let categories = {};
      for (let i = 0; i < res.data.length; i++) {
        let categorie = res.data[i].name;
        categories[categorie] = [];
        for (let r = 0; r < res.data[i].subcategories.length; r++) {
          categories[categorie].push(res.data[i].subcategories[r].name);
        }
      }
      this.setState({
        categories: categories,
        selectedCategorie: res.data[0].name
      });
    });
  }

  render() {
    return (
      <div className="CreateProduct">
        <div>
          <select
            onChange={e =>
              console.log(e.target.options[e.target.selectedIndex].text)
            }
          >
            {Object.keys(this.state.categories).map(categorie => (
              <option key={categorie} value={categorie}>
                {categorie}
              </option>
            ))}
          </select>

          <select>
            {this.state.categories[this.state.selectedCategorie] !== undefined
              ? this.state.categories[this.state.selectedCategorie].map(
                  subcategorie => (
                    <option key={subcategorie} value={subcategorie}>
                      {subcategorie}
                    </option>
                  )
                )
              : null}
          </select>

          <input type="text" placeholder="Name" />
          <textarea type="text" placeholder="Description" />
          <input type="text" placeholder="Price" />
          <input type="text" placeholder="Quantity" />
          <input type="text" placeholder="Thumbnail" />
        </div>
        <div className="attributes">
          <input type="text" placeholder="Brand" />
          <select>
            <option value="black">Black</option>
            <option value="gray">Gray</option>
            <option value="blue">Blue</option>
            <option value="green">Green</option>
            <option value="purple">Purple</option>
            <option value="pink">Pink</option>
            <option value="yellow">Yellow</option>
            <option value="orange">Orange</option>
            <option value="red">Red</option>
            <option value="brown">Brown</option>
            <option value="white">White</option>
            <option value="others">Others</option>
          </select>
          <input type="text" placeholder="Weight" />
          <input type="text" placeholder="Size" />
        </div>
        <div className="images">
          <input type="text" placeholder="Image URL" />
        </div>
      </div>
    );
  }
}
