import React, { Component } from "react";
import "./Register.css";
import axios from "axios";

export default class Register extends Component {
  state = {
    user: {
      name: "",
      surname: "",
      email: "",
      phone: "",
      address: "",
      username: "",
      password: ""
    }
  };

  postUserHandler() {
    axios.post(`http://localhost:8087/users`, {headers: {"Content-Type": "application/json"}}, {body: {...this.state.user}})
  }

  render() {
    return (
      <div className="Register">
        <header>
          <i
            className="fas fa-arrow-left"
            onClick={() => {
              this.props.toggleRegister();
            }}
          />
          <h1>Register</h1>
        </header>
        <div className="wrapper-register-fields">
          <input
            className="name"
            type="text"
            placeholder="Name"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, name: e.target.value }
              });
            }}
          />
          <input
            className="surname"
            type="text"
            placeholder="Surname"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, surname: e.target.value }
              });
            }}
          />
          <input
            className="email"
            type="email"
            placeholder="Email"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, email: e.target.value }
              });
            }}
          />
          <input
            className="phone"
            type="text"
            placeholder="Phone"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, phone: e.target.value }
              });
            }}
          />
          <input
            className="address"
            type="text"
            placeholder="Address"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, address: e.target.value }
              });
            }}
          />
          <input
            className="username"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, username: e.target.value }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, password: e.target.value }
              });
            }}
          />
          <button onClick={() => console.log(this.state.user)}>Register</button>
        </div>
      </div>
    );
  }
}
