import React, { Component } from "react";
import "./Users.css";
import axios from "axios";

export default class Users extends Component {
  state = {
    users: []
  };
  getCookie(cname) {
    let name = cname + "=";
    let ca = document.cookie.split(";");
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) === " ") {
        c = c.substring(1);
      }
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  }
  componentDidMount() {
    let url = `http://localhost:8087/secure/users?access_token=${this.getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      console.log(res.data);

      this.setState({
        users: res.data
      });
    });
  }
  render() {
    return (
      <div className="Users">
        {this.state.users.length === 0 ? null : (
          <table>
            <thead>
              <tr>
                <th id="username">Username</th>
                <th id="name">Name</th>
                <th id="email">Email</th>
                <th id="phone">Phone</th>
                <th id="role">Role</th>
                <th id="enable">Enable</th>
              </tr>
            </thead>
            <tbody>
              {this.state.users.map(user => (
                <React.Fragment>
                  <tr key={user.id}>
                    <td>{user.username}</td>
                    <td>
                      {user.name} {user.surname}
                    </td>
                    <td>{user.email}</td>
                    <td>{user.phone}</td>
                    <td>{user.roles[0].name}</td>
                    <td>
                      {user.enabled ? (
                        <div className="green" />
                      ) : (
                        <div className="red" />
                      )}
                    </td>
                  </tr>
                  <tr colspan="6" className="tes"  >
                    <div className="more-info" />
                  </tr>
                </React.Fragment>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}
