import React from "react";
import "./Modal.css";
import Backdrop from "../Backdrop/Backdrop";

export default function Modal(props) {
  let inputValue = "";
  return (
    <React.Fragment>
      <div className="Modal">
        <h2>Change Profile Image</h2>
        <input
          onChange={e => {
            inputValue = e.target.value;
          }}
          type="text"
          placeholder="Image URL"
        />
      </div>
      <button onClick={() => props.closeFullscreen(props.currentImgIndex)}>
        Update Image
      </button>
      <Backdrop {...props} />
    </React.Fragment>
  );
}
