import React, { Component } from "react";
import "./Products.css";

export default class Products extends Component {
  state = {
    products: []
  };
  async componentDidMount() {
    try {
      const response = await fetch (`http://localhost:8086/catalogs/categories/audio/subcategories/headphones`);
      const json = await response.json();
      this.setState({products: json});
    } catch(e) {
      console.log(e);
    }
  }

  render() {
    return (
      <div>
        <h2>Products</h2>
        <ul>
            {this.state.products.map(el => 
            <li>
              {el.name}
            </li>)}
        </ul>
      </div>
    );
  }
}
