import React, { Component } from "react";
import "./DropdownMenu.css";
//import NavigationItems from "../NavigationItems/NavigationItems";

export default class DropdownMenu extends Component {
  state = {
    categories: []
  };
  async componentDidMount() {
    const response = await fetch(`http://localhost:8086/catalogs/categories`);
    const json = await response.json();
    this.setState({ categories: json });
  }
  render() {
    return (
      <React.Fragment>

      <div class="DropdownMenu">
      <button class="dropbtn">Dropdown 
        <i class="fa fa-caret-down"></i>
      </button>
      <div class="dropdown-content">
        <div class="header">
          <h2>Mega Menu</h2>
        </div>   
        <div class="row">
          <div class="column">
            <h3>Category 1</h3>
            <a href="/">Link 1</a>
            <a href="/">Link 2</a>
            <a href="/">Link 3</a>
          </div>
        </div>
      </div>
    </div>
    <div class="DropdownMenu">
      <button class="dropbtn">Dropdown 
        <i class="fa fa-caret-down"></i>
      </button>
      <div class="dropdown-content">
        <div class="header">
          <h2>Mega Menu</h2>
        </div>   
        <div class="row">
          <div class="column">
            <h3>Category 1</h3>
            <a href="/">Link 1</a>
            <a href="/">Link 2</a>
            <a href="/">Link 3</a>
          </div>
        </div>
      </div>
    </div>  
      </React.Fragment>
    );
  }
}
