import React from "react";
import "./ProductsHome.css";

export default function ProductsHome(props) {
  return (
    <div
      className="ProductsHome"
      style={{ gridTemplateColumns: props.invert ? "2fr 3fr" : "3fr 2fr" }}
    >
      <img
        src={props.link}
        alt={props.title}
        style={{ gridColumn: props.invert ? "2 / span 1" : "1 / span 1" }}
      />
      <div className="info-product-home" style={{ gridColumn: props.invert ? "1 / span 1" : "2 / span 1", gridRow: props.invert ? "1 / span 1" : "1 / span 1"  }}>
        <h2>{props.title}</h2>
        <p>{props.description}</p>
      </div>
    </div>
  );
}
