import React, { Component } from "react";
import "./Users.css";
import Switch from "../UI/Switch/Switch";

import { connect } from "react-redux";
import { updateEnable, updateRole, fetchUsers } from "../../store/actions/index";

class Users extends Component {
  componentDidMount() {
    this.props.onfetchUsers();
  }
  render() {
    return (
      <div className="Users">
        {this.props.user.allUsers.length === 0 ? null : (
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Enable</th>
              </tr>
            </thead>
            <tbody>
              {this.props.user.allUsers.map((user, index) => (
                <React.Fragment key={user.id}>
                  <tr className="hover-row">
                    <td>{user.username}</td>
                    <td>
                      {user.name} {user.surname}
                    </td>
                    <td>{user.email}</td>
                    <td>
                      <select
                        value={user.roles[0].name}
                        onChange={e => {
                          this.props.onUserUpdateRole(
                            e.target.options[e.target.selectedIndex].text,
                            user,
                            index,
                            user.id
                          );
                        }}
                      >
                        <option value="ROLE_USER">User</option>
                        <option value="ROLE_ADMIN">Admin</option>
                      </select>
                    </td>
                    <td>
                      <Switch />
                      {user.enabled ? (
                        <div
                          className="green"
                          onClick={() =>
                            this.props.onUserUpdateEnable(
                              this.props.user.allUsers,
                              index,
                              user.id,
                              0
                            )
                          }
                        />
                      ) : (
                        <div
                          className="red"
                          onClick={() =>
                            this.props.onUserUpdateEnable(
                              this.props.user.allUsers,
                              index,
                              user.id,
                              1
                            )
                          }
                        />
                      )}
                    </td>
                    <td
                      className="dropdown-btn-wrapper"
                      onClick={() => {
                        let originalUsers = this.props.users;
                        originalUsers[index].isOpen = !originalUsers[index]
                          .isOpen;
                        this.setState({ users: originalUsers });
                      }}
                    >
                      <i className="fas fa-caret-down dropdown-btn" />
                    </td>
                  </tr>
                  {user.isOpen ? (
                    <tr>
                      <td colSpan="6">
                        <div className="more-info">
                          <div>
                            <img
                              src={user.img}
                              alt={user.username}
                              className="profile-img"
                            />
                          </div>
                          <div className="info">
                            <div className="id">
                              <span className="bold">Id:</span> {user.id}
                            </div>
                            <div className="phone">
                              <span className="bold">Phone:</span> {user.phone}
                            </div>
                            <div className="address">
                              <span className="bold">Address:</span>{" "}
                              {user.address}
                            </div>
                          </div>
                        </div>
                      </td>
                    </tr>
                  ) : null}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchUsers: () => dispatch(fetchUsers()),
    onUserUpdateEnable: (users, index, userId, val) =>
      dispatch(updateEnable(users, index, userId, val)),
    onUserUpdateRole: (role, user, index, userId) => dispatch(updateRole(role, user, index, userId))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Users);
