import React, { Component } from "react";
import "./Register.css";

export default class Register extends Component {
  state = {
    user: {
      name: "",
      email: "",
      username: "",
      password: ""
    }
  };

  updateUserHandler(e) {}

  render() {
    return (
      <div className="Register">
        <header>
          <i
            className="fas fa-arrow-left"
            onClick={() => {
              this.props.toggleRegister();
            }}
          />
          <h1>Register</h1>
        </header>
        <div className="wrapper-register-fields">
          <input
            className="name"
            type="text"
            placeholder="Name"
            onChange={e => {
              this.setState({
                user: { name: e.target.value, ...this.state.user.name }
              });
            }}
          />
          <input
            className="email"
            type="email"
            placeholder="Email"
            onChange={e => {
              this.setState({
                user: { email: e.target.value, ...this.state.user.name }
              });
            }}
          />
          <input
            className="user"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { username: e.target.value, ...this.state.user.name }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { password: e.target.value, ...this.state.user.name }
              });
            }}
          />
          <button>Register</button>
        </div>
      </div>
    );
  }
}
